var focusableElements = [];
var firstFocusableElement = null;
var lastFocusableElement = null;

function openNav() {
  var navDrawer = document.getElementById('navDrawer');
  var navOverlay = document.getElementById('navOverlay');

  navDrawer.classList.add('open');
  navOverlay.classList.add('show');
  document.body.style.overflow = 'hidden';

  // Get all focusable elements in nav drawer
  focusableElements = navDrawer.querySelectorAll('a[href], button:not([disabled]), [tabindex]:not([tabindex="-1"])');
  if (focusableElements.length > 0) {
    firstFocusableElement = focusableElements[0];
    lastFocusableElement = focusableElements[focusableElements.length - 1];

    // Focus first element
    setTimeout(function() {
      firstFocusableElement.focus();
    }, 100);
  }
}

function closeNav() {
  var navDrawer = document.getElementById('navDrawer');
  var navOverlay = document.getElementById('navOverlay');

  navDrawer.classList.remove('open');
  navOverlay.classList.remove('show');
  document.body.style.overflow = '';
}

function toggleSubmenu(menuId) {
  var submenu = document.getElementById('submenu-' + menuId);
  var chevron = document.getElementById('chevron-' + menuId);
  var button = document.querySelector('[aria-controls="submenu-' + menuId + '"]');

  if (submenu.classList.contains('open')) {
    submenu.classList.remove('open');
    chevron.classList.remove('rotate');
    if (button) button.setAttribute('aria-expanded', 'false');
  } else {
    submenu.classList.add('open');
    chevron.classList.add('rotate');
    if (button) button.setAttribute('aria-expanded', 'true');
  }
}

// Keyboard navigation with focus trapping
document.addEventListener('keydown', function(e) {
  var navDrawer = document.getElementById('navDrawer');
  var isOpen = navDrawer.classList.contains('open');

  // Close on Escape
  if (e.key === 'Escape' && isOpen) {
    closeNav();
    return;
  }

  // Focus trapping
  if (isOpen && e.key === 'Tab') {
    if (e.shiftKey) { // Shift + Tab
      if (document.activeElement === firstFocusableElement) {
        e.preventDefault();
        lastFocusableElement.focus();
      }
    } else { // Tab
      if (document.activeElement === lastFocusableElement) {
        e.preventDefault();
        firstFocusableElement.focus();
      }
    }
  }
});

// Search Overlay Functions
function openSearch() {
  var searchOverlay = document.getElementById('searchOverlay');
  var searchInput = document.getElementById('searchInput');
  searchOverlay.classList.add('active');
  document.body.style.overflow = 'hidden';

  // Focus search input after a short delay
  setTimeout(function() {
    searchInput.focus();
  }, 100);
}

function closeSearch() {
  var searchOverlay = document.getElementById('searchOverlay');
  searchOverlay.classList.remove('active');
  document.body.style.overflow = '';
}

function closeSearchIfClickOutside(event) {
  if (event.target.id === 'searchOverlay') {
    closeSearch();
  }
}

// Close search on Escape key
document.addEventListener('keydown', function(e) {
  if (e.key === 'Escape') {
    var searchOverlay = document.getElementById('searchOverlay');
    if (searchOverlay && searchOverlay.classList.contains('active')) {
      closeSearch();
    }
  }
});

// Cookie Consent Functions
function acceptCookies() {
  localStorage.setItem('cookieConsent', 'accepted');
  document.getElementById('cookieConsent').classList.remove('show');
  // Load analytics/ads if needed
  console.log('Cookies accepted');
}

function declineCookies() {
  localStorage.setItem('cookieConsent', 'declined');
  document.getElementById('cookieConsent').classList.remove('show');
  console.log('Cookies declined');
}

// Show cookie banner if not answered
window.addEventListener('load', function() {
  var consent = localStorage.getItem('cookieConsent');
  if (!consent) {
    setTimeout(function() {
      document.getElementById('cookieConsent').classList.add('show');
    }, 1000);
  }
});
